            <section class="forgot-password__section">
                <div class="forgot-password__section__form-container">

                    <h1 class="forgot-password__section__title">
                        Reset Password
                    </h1>
                    <p class="forgot-password__section__paragraph">
                        Nulla pulvinar, ex elementum pulvinar feugiat, ligula neque pretium risus, ut eleifend.
                    </p>

                    <form class="forgot-password__section__form">

                        <!-- Email -->
                        <div class="forgot-password__section__form__row">
                            <div class="form-input form-input--email form-input--icon">
                                <label for="email" class="form-input__label">
                                    Email
                                </label>
                                <input class="form-input--email__field" id="email" autocomplete="off" name="email" type="email" placeholder="user@mail.com">
                                <span class="form-input__icon">
                                    <svg class="feather">
                                        <use href="./src/icons/feather-sprite.svg#mail" />
                                    </svg>
                                </span>
                            </div>
                        </div>

                        <div class="forgot-password__section__form__row">
                            <button class="btn--primary forgot-password__section__form__btn-submit" type="submit">
                                Send reset link
                            </button>
                        </div>
                        <div class="forgot-password__section__form__row">
                            <a href="./" class="btn--outlined forgot-password__section__form__btn-submit">
                                Go back
                            </a>
                        </div>
                    </form>
                </div>
            </section>
